import './App.css';

import { Row, Col } from 'react-flexbox-grid';
import { useEffect, useState } from 'react';


function App() {

  const [text,setText]=useState("")

  const encode=(value)=>{

    const arr = Array.prototype.map.call(value, function (char) {
      return char.charCodeAt(0);
    });
  }

  return (
    <>
    <Row style={{height:"100vh"}}>
      <Col  xs={3} md={3}>
        <textarea 
          cols={500} style={{height:"90vh",width:"90%",margin:"10px 10px 10px 10px ", padding:"10px 10px 10px 10px"}}
          value={text}
          onChange={(event)=>setText(event.target.value)}
        /></Col>
      <Col  xs={9} md={9} >

      <img  style={{height:"90vh",width:"90%",margin:"10px 10px 10px 10px ", padding:"10px 10px 10px 10px"}}
      alt="generated image"
      src="http://t420.doublechaintech.cn:2080/plantuml/svg/~h407374617274756d6c0a416c6963652d3e426f62203a204920616d207573696e67206865780a40656e64756d6c"/>
      </Col>
    </Row>
    </>
  );
}

export default App;
